import React, { useEffect, useRef } from "react";
import { createPortal } from "react-dom";
import { WelcomeModal } from "./";



















const modalRoot = document.getElementById("modal");

export const Modal = ({ children }) => {
  const elRef = useRef(null);
  if (!elRef.current) {
    const div = document.createElement("div");
    elRef.current = div;
  }

  useEffect(() => {
    modalRoot.appendChild(elRef.current);

    return () => modalRoot.removeChild(elRef.current);
  }, []);

    return createPortal(<Content>
        <ModalWrapper>
            <img
                src={Illustrations.Welcome}
                alt="Site welcome"
                aria-hidden="true"
            />
            <WelcomeHeader>Hey there, welcome to my site!</WelcomeHeader>
            <WelcomeText>
                I made this site with the intent to introduce myself, as well as
                showcase my front-end development skills.
        </WelcomeText>
            <PrimaryButton onClick={toggleModal()} aria-label="Close modal">
                I'm in, show me what you've got!
        </PrimaryButton>
            <CloseModalButton onClick={toggleModal()} aria-label="Close modal">
                <CloseIcon />
            </CloseModalButton>
        </ModalWrapper>
    </Content>, elRef.current);
};
